import React from "react";
import styles from "./SingleItem.module.css";
import { useParams } from 'react-router-dom'
import {useSelector,useDispatch} from 'react-redux'



const ViewItems = ({ current, addToCart }) => {

    const {id}=useParams();
    const Full_data=useSelector((state)=>state.products)

    const addToCart1=()=>{
        dispatch({
            type:"ADD_TO_CART"
        })



  return (
      <>
      {



      Full_data.filter((tempid)=>{return tempid.id===parseInt(id)}).map((current=>{
          return(
            <div className={styles.singleItem}>

            <div className={styles.singleItem__details}>
              <div>
                <h4 >{current.bot}</h4>
                <div >{current.description}</div>

              </div> 


              <div className={styles.index_val}>
                  <p>index value</p>
                <div>
                $ {current["index-value"]}


                </div>
                </div>

                <div className={styles.cagr_val}>

                    <p>Carg</p>
                  <div>
                  $ {current["cagr"]}

                  </div>
                  </div>
                <div>
                </div>





            </div>
            <div className={styles.but_class}> 

              <button
                onClick={addToCart1()}
                className={styles.details__addBtn}
                >
                Cart
              </button>
            <button className={styles.details__addBtn}>ModerateRisk</button>
            </div>
          </div>




          )
    }))
}

    </>
    );
};
}
export default ViewItems;
